\newcommand{\Twire}[3][]{
\begin{scope}[shift={({0.2*cos(45+#1)},{0.2*sin(45+#1)})}]
\draw[#3] (0,0)--(1,0)--(2,-1)--(2,0)--(2,1)--(1,0)--(0,0);
\draw[#3] (2,0)--(2,1) (3,0)--(3,1);
\fill[#3] (1,0) circle (0.1);
\fill[#3] (2,0) circle (0.1);
\fill[#3] (0,0) circle (0.1);
\fill[#3] (2,1) circle (0.1);
\fill[#3] (3,1) circle (0.1);
\node (A#2) at (2,-1.2) [circle,fill,inner sep=1pt]{};
\node (B#2) at (2,1.2) [circle,fill,inner sep=1pt]{};
\end{scope}
}
\newcommand{\Trochoid}[3][]{
\begin{scope}[shift={({0.2*cos(45+#1)},{0.2*sin(45+#1)})}]
\draw[#3] (0,0)--(1,0)--(2,-1)--(2,0)--(2,1)--(1,0)--(0,0);
\draw[#3] (2,0)--(2,1) (3,0)--(3,1);
\fill[#3] (1,0) circle (0.1);
\fill[#3] (2,0) circle (0.1);
\fill[#3] (0,0) circle (0.1);
\fill[#3] (2,1) circle (0.1);
\fill[#3] (3,1) circle (0.1);
\fill[#3] (2,-1) circle (0.1);
\node (A#2) at (2,-1.2) [circle,fill,inner sep=1pt]{};
\node (B#2) at (2,1.2) [circle,fill,inner sep=1pt]{};
\node (C#2) at (1,0) [circle,fill,inner sep=1pt]{};
\node (D#2) at (3,0) [circle,fill,inner sep=1pt]{};
\end{scope}
}
\newcommand{\Bissection}[3][]{
\begin{scope}[shift={({0.2*cos(45+#1)},{0.2*sin(45+#1)})}]
\draw[#3] (0,0)--(1,0)--(2,-1)--(2,0)--(2,1)--(1,0)--(0,0);
\draw[#3] (2,0)--(2,1) (3,0)--(3,1);
\fill[#3] (1,0) circle (0.1);
\fill[#3] (2,0) circle (0.1);
\fill[#3] (0,0) circle (0.1);
\fill[#3] (2,-1) circle (0.1);
\fill[#3] (3,-1) circle (0.1);
\fill[#3] (2,1) circle (0.1);
\node (A#2) at (2,-1.2) [circle,fill,inner sep=1pt]{};
\node (B#2) at (2,1.2) [circle,fill,inner sep=1pt]{};
\node (C#2) at (1,0) [circle,fill,inner sep=1pt]{};
\node (D#2) at (3,0) [circle,fill,inner sep=1pt]{};
\end{scope}
}
