        \node[above right, inner sep=0] at (0,1.6*#4) {\huge #2};
        \node at (#1,1.8*#4) [above right, inner sep=0] {\huge #2};
        \node at (0.5*#1,1.8*#4) [above right, inner sep=0] {\huge #2};
    \end{scope}
}

\newcommand{\skillwindow}[5] % position (left top), width, height, color, text, window size
{   \begin{scope}[shift={#1}, rotate=#2]
