        \node at ($(0.25*#1,0.5*#5)+(0.75*#4,0)$) [above] {\huge #2};
    \end{scope}
}

\newcommand{\manorskiv}[3] % position (left top), width, height, color, text
{   \begin{scope}[shift={#1}, rotate=#2]
