      \foreach \s in {1,...,10}
      {
        \draw (\s*360/10:8) circle (2);
        \fill (\s*360/10:8) circle (2pt);
      }
      \foreach \s in {1,...,5}
      {
        \pgfmathsetmacro{\x}{-(\s-1)/10+1/2}
        \pgfmathsetmacro{\y}{\s/5-4/5}
        \pgfmathsetmacro{\t}{\s*360/10+180/8}
        \pgfmathsetmacro{\a}{\s*360/10+90/8}
        \pgfmathsetmacro{\b}{\s*360/10+162/8}
        \pgfmathsetmacro{\c}{\s*360/10+234/8}
        \pgfmathsetmacro{\d}{\s*360/10+306/8}
        \draw [double distance=3pt] (\x,\y) -- (\t:5);
        \node [circle,fill=black,inner sep=0pt,minimum size=1.8mm] at (\t:5) {};
        \node at (\a:4.8) {$u_{\s}$};
        \node at (\b:3.3) {$u_{\s}$};
        \node at (\c:3.3) {$u_{\s}$};
        \node at (\d:4.8) {$u_{\s}$};
      }
      \draw (1,0) arc (0:360/10:8);
      \node at (0.5,7) {$\overline{K}_5$};
    \end{scope}
    \begin{scope}[even odd rule,rotate=180/10]
      \clip (-1,-1) rectangle (9,9);
      \foreach \s in {1,...,10}
      {
        \draw (\s*360/10:8) circle (2);
        \fill (\s*360/10:8) circle (2pt);
      }
      \foreach \s in {1,...,5}
      {
        \pgfmathsetmacro{\x}{-(\s-1)/10+1/2}
        \pgfmathsetmacro{\y}{\s/5-4/5}
        \pgfmathsetmacro{\t}{\s*360/10+180/8}
        \pgfmathsetmacro{\a}{\s*360/10+90/8}
        \pgfmathsetmacro{\b}{\s*360/10+162/8}
        \pgfmathsetmacro{\c}{\s*360/10+234/8}
        \pgfmathsetmacro{\d}{\s*360/10+306/8}
        \draw [double distance=3pt] (\x,\y) -- (\t:5);
        \node (u\the\numexpr\s-1) [circle,fill=black,inner sep=0pt,minimum size=1.8mm] at (\t:5) {};
        \ifnum\s=1
          \node [above=2pt] at (\t:5) {$\overline{K}_4$};
        \fi
        \node [circle,fill=black,inner sep=0pt,minimum size=1.8mm] at (\t:8) {};
      }
    \end{scope}
    \draw [shorten >= 3pt] (0,0) circle (6);
    \node at (1/2,-3) {$\overline{K}_3$};
