% a few commands
    \newcommand{\DrawCylinder}[5]{
        \begin{scope}[canvas is #1]
            \addplot3[#4, % style
                fill opacity=0.9,
                fill=#2,
                faceted color=#2,
                line width=0.5mm,
                % first point is the top of the cone
                point meta max=1,
                % number of slices is in the fourth coordinate
                % face width is in the fifth coordinate
                samples=51,
                domain=0:360,
                variable=\t,
            ] 
                ({#3*cos(\t)},{#3*sin(\t)},{#5});
        \end{scope}
    }
    \newcommand{\DrawTopCircle}[4]{
        \DrawCylinder{canvas}{1.0}{7.5}{#3}{#2}
        \DrawCylinder{canvas}{0}{7}{#1}{#2}
        \draw[dashed,#4,fill opacity=0.2,#1] (0,0,0) circle (5cm);
    }
    \newcommand{\DrawSideCircle}[4]{
        \DrawCylinder{canvas}{1.0}{2.5}{#3}{#2}
        \draw[dashed,#4,fill opacity=0.2,#1] (0,0,0) circle (2.5cm);
    }

% definitions
