    \newcommand{\AngleB}{50} % angle at which the upper slice is presented
    \newcommand{\AngleC}{80} % angle at which the cylinder is rotated
    \newcommand{\Offset}{10} % offset in the z direction
    \newcommand{\OffsetTwo}{5} % offset in the z direction

