        \colorlet{halfstyle}{orange!50!red!50!white}
        \colorlet{normalstyle}{red!90!black!90}

        % the cone
        \DrawCylinder{half}{\r}{\AngleA}{halfstyle}
        \DrawCylinder{half}{\r}{\AngleA}{halfstyle}
        \DrawCylinder{half}{\r}{\AngleB}{halfstyle}
        \DrawCylinder{half}{\r}{\AngleB}{halfstyle}

        \DrawTopCircle{\r}{\h}{\phivec}{normalstyle}
        \DrawTopCircle{\r}{\h}{\AngleC}{normalstyle}

        % the four directions
        \draw[-latex,very thick,gray] (0,0,0) -- (0.5*\r,0,0);
        \draw[-latex,very thick,gray] (0,0,0) -- (0,0.5*\r,0);
        \draw[-latex,very thick,gray] (0,0,0) -- (0,0,0.5*\r);
        \draw[-latex,very thick,gray] (0,0,0) -- (0,0,0.5*\r+\OffsetTwo);

        % the filled cones
        \begin{scope}[canvas is normal plane at z=-2]
            \coordinate (a) at (0,0);
            \coordinate (b) at (8,0);
            \DrawTopCircle{\r}{4}{90}{topstyle}
