        \draw (0,0,\h) circle (\r);
        \draw (0,0,\h) -- +(0,\h+\Offset,\Offset);
        \draw (\r,0,\h) arc (0:\Angle:\r);
        \draw[dashed] (0,0,\h) -- (0,0,-\h);
        \foreach \t in {0,8,...,359} {
            \filldraw (\t:\r) circle (1pt);
        }
        \node[anchor=north west] at (0,0,\h+\Offset) {Fold Point};
        \node[anchor=south west] at (0,0,0) {${\cal C}$};
