\newcounter{tmnt}
\newcommand{\Tmnt}[2][1]{\stepcounter{tmnt}%
\node[#1] (t\thetmpnt) at (0,-1) {\alph{tmnt}};%
\node[#1] (b\thetmpnt) at (0,-3) {$\times$};%
\begin{scope}[shift={(-1,0)},xscale=1.5]
\node[#1] (r\thetmpnt) at (0,0) {$\times$};
\node[#1] (l\thetmpnt) at (0,-2) {};
\draw (0,0) ellipse (1.5 and 0.5);
\draw[dotted] (-0.5,-2) -- (0.5,-2);
\end{scope}
\coordinate (p\thetmpnt) at (b\thetmpnt.south east);%
\path (t\thetmpnt) -- (b\thetmpnt) node[midway] {$x_i$};
\path (t\thetmpnt) -- (l\thetmpnt) node[midway] {$\vec{r}_i$};
\path (l\thetmpnt) -- (b\thetmpnt) node[midway] {$\vec{r}_{i+1}$};
}

\newcommand{\TmntText}[4][1]{%
\ifthenelse{#4=1}{\draw (p#2) -- (t#3);}{\draw (p#2) -- (l#3);}
\draw (b#2) -- (r#3);
\begin{scope}[shift={($(t#3)-(0,2)$)}]
\path (r#3) -- (b#3) node[midway] {#4};
\end{scope}
\coordinate (p#3) at (r#3);
\path (t#3) -- (b#3) node[midway] {$x_{i+1}$};
}

\newcommand{\TmntSpecial}[3][1]{\stepcounter{tmnt}%
\pgfmathtruncatemacro{\tst}{#3+1}%
