\newcommand\decorationsgoldenequilibrating{%
  \tikzset{%
    golden circle/.style={circle, radius=#1, draw, yellow!60!black, decoration={name=eqilibrating circles, mirror, amplitude=1.5mm}, decorate},
    /pgf/decoration/amplitude/.code=\pgfmathsetlengthmacro\pgfdecorationamplitude{#1},
  }
  \def\pgfdecorationamplitude{1mm}
  \pgfdeclaredecoration{equilibrating circles}{start}
  {
    \state{start}[width=+0pt,next state=shape,
      persistent precomputation={\pgfmathparse{\pgfdecoratedremainingdistance/\pgfdecorationamplitude}
      \let\pgfdecorationsegmentlength=\pgfmathresult}]{
      \coordinate (last circle hit) at (0, 0);
      \let\pgfdecoratedpathlength=\pgfdecorationsegmentlength
    }
    \state{shape}[width=\pgfdecorationsegmentlength]{
      \ifdim\pgfdecoratedremainingdistance<\pgfdecorationamplitude
      \path let \p1 = (last circle hit), \p2 = (0, 0)
        in (\x1, \y1) -- (\x2, \y2) coordinate (last circle);
      \pgfpathcircle{\pgfpointorigin}{\pgfdecorationamplitude/2}
      \pgfusepath{stroke}
      \pgfmathparse{int(\pgfkeysvalueof{/pgf/decoration/number of circles})}
      \let\pgfdecorationsegmentcount=\pgfmathresult
      \pgfmathloop
      \ifnum\pgfmathcounter>\pgfdecorationsegmentcount
      \else
      \pgfpathcircle{(\pgfpointlastonpath)},{\pgfdecorationamplitude/2}
      \repeatpgfmathloop
      \path let \p1 = (last circle), \p2 = (0, 0)
        in (\x1, \y1) -- (\x2, \y2) coordinate (last circle end);
      \pgfmathparse{int(\pgfdecoratedcompleteddistance/\pgfdecorationsegmentlength)}
      \let\pgfdecorationsegmentcount=\pgfmathresult
      \pgfmathloop
      \ifnum\pgfmathcounter>\pgfdecorationsegmentcount
      \else
      \pgfpathcircle{(\pgfpointlastonpath)},{-\pgfdecorationamplitude/2}
      \repeatpgfmathloop
      \path let \p1 = (last circle), \p2 = (0, 0)
        in (\x1, \y1) -- (\x2, \y2) coordinate (last circle hide) ;
      \pgfmathloop
      \ifnum\pgfmathcounter>\pgfdecorationsegmentcount
      \else
      \pgfpathcircle{(\pgfpointlastonpath)},{-\pgfdecorationamplitude}
      \repeatpgfmathloop
      \pgfusepath{stroke}
    }
    \state{hide}[width=+\pgfdecorationsegmentlength]
    {
      \pgfpointtransformed{\pgfpointanchor{last circle hide}{center}}
