          \let\myangle=\pgfmathresult
          \pgfmathanglebetweenpoints{\pgfpointanchor{q-#1}{east}}{\pgfpointanchor{q-0}{base}}
          \let\myangle=\pgfmathresult
          \pgfmathparse{int(max(0,min(\myangle-\myangle+90,\myangle-90,180+2*\myangle)))}
