          \let\midangle=\pgfmathresult
          \pgfmathparse{int(min(\midangle,\myangle+180))}
          \let\midangle=\pgfmathresult
          \pgfmathparse{\midangle+180}
          \let\midangle=\pgfmathresult
        \end{scope}
        \foreach \i in {0,...,#1}
        {%
          \pgfmathparse{\midangle+180}
          \let\midangle=\pgfmathresult
