\tikzset{
  vector outwards/.style args={#1 with #2}{
    decoration={
      markings,
      mark=between positions 0 and 1 step 0.3 with {
        \pgfmathsetmacro{\V}{{\V}[\pgfkeysvalueof{/pgf/decoration/mark info/sequence number}-1]}}
      % The position of the vector at the previous step
      % is \prevpos.
      \pgfmathsetmacro{\prevpos}{#1}
      \pgfmathtruncatemacro{\itest}{abs(\V) <.1? 1 : 0}
      \ifnum\itest=0
        \ifdim\prevpos pt > 0pt
          \def\mystart{e-#2}
        \else
          \def\mystart{a-#2}
        \fi
        \def\myend{#2}
      \else
        \ifdim\prevpos pt > \V pt
          \def\mystart{f-#2}
        \else
          \def\mystart{c-#2}
        \fi
        \def\myend{e-#2}
      \fi
      \draw[ultra thin] (0,0) -- (\prevpos pt,\prevpos pt);
    }
  },
  vector inwards/.style args={#1 with #2}{
    decoration={
      markings,
      mark=between positions 0 and 1 step 0.3 with {
        \pgfmathsetmacro{\V}{{\V}[\pgfkeysvalueof{/pgf/decoration/mark info/sequence number}-1]}}
      % The position of the vector at the previous step
      % is \prevpos.
      \pgfmathsetmacro{\prevpos}{#1}
      \pgfmathtruncatemacro{\itest}{abs(\V) <.1? 1 : 0}
      \ifnum\itest=0
        \ifdim\prevpos pt > 0pt
          \def\mystart{e-#2}
        \else
          \def\mystart{a-#2}
        \fi
        \def\myend{#2}
      \else
        \ifdim\prevpos pt > \V pt
          \def\mystart{f-#2}
        \else
          \def\mystart{c-#2}
        \fi
        \def\myend{e-#2}
      \fi
      \draw[ultra thin] (0,0) -- (\prevpos pt,-\prevpos pt);
      \pgfmathsetmacro{\myop}{sign(\myend-\mystart)}
      \ifnum\myop<0
        \def\mysign{-}
      \else
        \def\mysign{+}
      \fi
      %\node[above] at (\mypos, \mypos) {\mystart,\mysign\myend};
      \node[above] at (\mypos pt,\mypos pt) {\mystart,\mysign\myend};
      %\node[above] at (\mypos pt,\mypos pt) {\mystart,\mysign};
      \node[circle, fill, inner sep=0, minimum size=0, font=\tiny] (\mystart) {};
      \ifx\mystart\empty%empty for now
      \else
        \pgfmathsetmacro{\pos}{min(\mysign*\myend/abs(\myend-\mystart)+.5,.5)}
        \pgfmathsetmacro{\ang}{atan2(\mysign,\myend)}
        \path (\prevpos pt,\mypos pt) -- (\pos pt,0)
        node[pos=\pos-\prevpos pt, rotate=\ang, font=\tiny] {#1}; %#2
      \fi
    }
  }
}
