\pgfdeclareshape{chip}{
    \inheritsavedanchors[from=rectangle] % this is nearly a rectangle
    \inheritanchorborder[from=rectangle]
    \inheritanchor[from=rectangle]{center}
    \inheritanchor[from=rectangle]{north}
    \inheritanchor[from=rectangle]{south}
    \inheritanchor[from=rectangle]{west}
    \inheritanchor[from=rectangle]{east}
    \inheritanchor[from=rectangle]{north west}
    \inheritanchor[from=rectangle]{south west}
    \inheritanchor[from=rectangle]{north east}
    \inheritanchor[from=rectangle]{south east}
    \inheritanchor[from=rectangle]{center west}
    \inheritanchor[from=rectangle]{center east}
    \inheritanchor[from=rectangle]{east west}
    \inheritanchor[from=rectangle]{input}
    \inheritanchor[from=rectangle]{output}
    \inheritanchorborder[from=rectangle]

    \inheritbackgroundpath[from=rectangle]

    \foregroundpath{
        \setkeys{chip pin spacing}{\pgfkeysvalueof{/pgf/chip pins}-\pgfkeysvalueof{/pgf/chip pin spacing}}
        \pgfmathsetmacro\chippins{\pgfkeysvalueof{/pgf/chip pins}-1}
        \def\chipreference{chip[\pgfkeysvalueof{/pgf/chip pins}][<pin number>]}
        \foreach \pin in {0,...,\chippins} {
            \pgfmathsetmacro{\chippang}{\pin*\pgfkeysvalueof{/pgf/chip pin spacing}}
            \setlength{\pgf@xc}{\pgfkeysvalueof{/pgf/chip pins}*\chippinwidth/2+\chipreference+\chippang}
            \ifdim\pgf@xc<\pgfkeysvalueof{/pgf/chip pins}*\pgfkeysvalueof{/pgf/chip pin spacing}
                \pgfutil@ifundefined{pgf@anchor@chip@pin@\pgfkeysvalueof{/pgf/chip pins}@\chipreference}{}{
                    \node[draw=none,
                         pin=\chipreference-\pgfkeysvalueof{/pgf/chip pins}+\chippang+90:\pgfkeysvalueof{/pgf/chip pins}](\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\pgfkeysvalueof{/pgf/chip pins}+\chippang){};
                }
            }{}
            \ifodd\pgf@anchor@chip@pin@\relax
                \def\outputlabel{0}
                \def\inputlabel{1}
                \def\pinlabel{1}
            \else
                \def\outputlabel{0}
                \def\inputlabel{-1}
                \def\pinlabel{-1}
            \fi
            \ifnum\chippang>0
                \pgfutil@ifundefined{pgf@anchor@chip@pin@\pgfkeysvalueof{/pgf/chip pins}@\chippang}{
                    \node [inner sep = 0pt] (\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\chippang+\chippins/2+\chippang/2+\outputlabel+\inputlabel+\pinlabel) at (\pgfkeysvalueof{/pgf/chip pins}-\chippang/2+\chippins/2+\outputlabel+\inputlabel+\pinlabel){chip[rotate=90](@{\chippang+1/2}0)node{\resizebox{-3pt}{!}{\usebox\picbox}}};
                }{}
                {}
            \fi
            \setlength{\pgf@xc}{\pgfkeysvalueof{/pgf/chip pins}*\chippins/2+\chipreference+\chippang}
            \ifdim\pgf@xc<0.1\pgfutil@ifundefined{pgf@anchor@chip@pin@\pgfkeysvalueof{/pgf/chip pins}+\chippang}{
                \node [inner sep = 0pt](\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\chippang-1+\chippins/2+\chippang/2+\outputlabel+\inputlabel+\pinlabel) at (\pgfkeysvalueof{/pgf/chip pins}-\chippang/2+\chippins/2+\outputlabel+\inputlabel+\pinlabel){chip[rotate=90](@{\chippang+1/2}0)node{\resizebox{3pt}{!}{\usebox\picbox}}};
            }{}
            \ifnum\chippang>0
                \pgfutil@ifundefined{pgf@anchor@chip@pin@\chippang}{
                    \node [inner sep = 0pt](\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\chippang-1+\chippins/2+\chippang/2+\outputlabel+\inputlabel+\pinlabel) at (\pgfkeysvalueof{/pgf/chip pins}-\chippang/2+\chippins/2+\outputlabel+\inputlabel+\pinlabel){chip[rotate=90](@{\chippang+1/2}0)node{\resizebox{3pt}{!}{\usebox\picbox}}};
                }{}
                {}
            \fi
            \ifnum\chippang<\chippins
                \pgfutil@ifundefined{pgf@anchor@chip@pin@\chippang}{
                    \node [inner sep = 0pt](\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\chippang-\chippins+\chippins/2+\chippang/2+\outputlabel+\inputlabel+\pinlabel) at (\pgfkeysvalueof{/pgf/chip pins}-\chippang/2+\chippins/2+\outputlabel+\inputlabel+\pinlabel){chip[rotate=90](@{\chippang+1/2}0)node{\resizebox{3pt}{!}{\usebox\picbox}}};
                }{}
                {}
            \fi
            \ifnum\chippang>\chippins
                \pgfutil@ifundefined{pgf@anchor@chip@pin@\chippang}{
                    \node [inner sep = 0pt](\pgfkeysvalueof{/pgf/chip pins}-\chipreference-\chippang-\chippins+\chippins/2+\chippang/2+\outputlabel+\inputlabel+\pinlabel) at (\pgfkeysvalueof{/pgf/chip pins}-\chippang/2+\chippins/2+\outputlabel+\inputlabel+\pinlabel){chip[rotate=90](@{\chippang+1/2}0)node{\resizebox{3pt}{!}{\usebox\picbox}}};
                }{}
                {}
            \fi
        }
    }
}
