\newcommand\hexapavage[3][]{% 1st arg: tag, 2nd arg: symmetries, 3rd arg: groups
  \begin{scope}[scale=0.75]
    \pgfmathsetmacro\x{#3}
    \pgfmathsetmacro\y{#2}
    \coordinate (centro) at (0,2*#2);
    \foreach [count=\i from 0] \sym in {#3} {
      \pgfmathsetmacro\pos{1.33*Mod(\i-1,4)}
      \coordinate (\sym) at ({Mod(\i-1,2)*180+#1*90}, \y+#2);
    }
    \foreach \s in {#3} {
      \pgfmathsetmacro\c{int(mod(5+2*\s-1,\x))}
      \ifthenelse{\c<5}{\draw[ultra thick,black] (\s) -- (centro);}{}  
    } 
    \foreach \s in {#3} {
      \pgfmathsetmacro\c{-int(mod(\s+1,\x))}
      \ifthenelse{\c<5}{\draw[ultra thick,black] (\s) -- (centro);}{}  
    } 
    \foreach \s in {#3} {
      \pgfmathsetmacro\c{int(mod(\s+3,\x))}
      \ifthenelse{\c<5}{\draw[ultra thick,black] (\s) -- (centro);}{}  
    }
  \end{scope}
}

\newcommand\hexapage[2][]{% 1st arg: tag, 2nd arg: groups
  \begin{scope}[scale=0.75]
    \pgfmathsetmacro\x{#2}
    \pgfmathsetmacro\y{#1}
    \coordinate (centro) at (0,2*#1);
    \foreach [count=\i from 0] \group in {#2} {
      \pgfmathsetmacro\pos{1.33*Mod(\i,4)}
      \coordinate (gr\i) at ({Mod(\i,2)*180+#1*90}, \y+#1);
      \hexapavage{gr\i}{\group}
    }
    \foreach [count=\i from 0] \sym in {#1} {
      \pgfmathsetmacro\pos{1.33*Mod(\i,4)}
      \coordinate (sp\i) at ({Mod(\i,2)*180+#1*90}, \y+#1/2);
      \hexapavage[0]{sp\i}{\sym}
    }
  \end{scope}
}

