% Newcommand

%% Right Angle
\newcommand{\RightAngle}[4][4pt]{%
	\draw[shift={#3},rotate=#2] (0,0) -- ++ (#1:1) -- ++ (#2+180:#4) -- cycle;
}

% Styles
%% To Set the Style for the Noses
\tikzset{%
	Nozzle/.style={inner sep=0pt,outer sep=0pt,minimum width=1.5mm,minimum height=1mm, draw=black, fill=white},
	Nozzle Guide/.style={inner sep=0pt,outer sep=0pt,minimum width=1.5mm, minimum height=10mm, draw=black, fill=black},
	Nozzle Label/.style={inner sep=0pt,outer sep=0pt,minimum width=1.5mm, minimum height=10mm, draw=none, fill=white, text=black, font=\scriptsize\sffamily},
	Flange/Arrow/.style={draw=none, line width=0.5mm, -stealth, line cap=round},
	Flanges/.style={draw=none, line width=0.5mm, -stealth, line cap=butt},
	Radius/.style={draw=none, line width=0.5mm},
	% Main Rectangle
	Rectangle/.style={draw=black, thick, fill=white, rounded corners}
}

\newcommand{\Cable}[4]{
	% [options], start coordinate, length, width
	\draw[thick, #1] (#2) +(-#4/2,#3/2) -- +(#4,#3) -- +(#4,-#4+#3) node[Nozzle Guide] {};
	\draw[thick, #1] (#2) +(-#4/2,-#3/2) -- +(#4,#3) -- +(#4,-#4+#3) node[Nozzle Guide] {};
}

\newcommand{\Valve}[2]{
	% [options], start coordinate: position
	% [minimum width, minimum height]
	\node[Nozzle, rotate={90+#1*180/100}, minimum width={#2*2}, minimum height={#2*2}] at (#1) ( valve) {};
	\coordinate (l valve) at (0, valve.south);	
}

\newcommand{\NozzleLabel}[2]{
	%% Start at the tip and move down
	\draw[Nozzle Label] (0,0) -- node[Nozzle Label, sloped, anchor=south] {#1} (0,-#2);
}

\newcommand{\DrawConnection}[1]{
	% Draw connection.
	\draw[Flanges] (0, 0) -- (1, 0) node[Nozzle Label={below:#1}]{};
	\draw[Flanges] (0, -1) -- (1, -1) node[Nozzle Label={below:#1}]{};
	\draw[Flanges] (0, -2) -- (1, -2) node[Nozzle Label={below:#1}]{};
}

