\def\layersep{2.5cm}

\begin{tikzpicture}[
    shorten >=1pt,->,
    draw=black!50,
    node distance=\layersep,
    every pin edge/.style={<-,shorten <=1pt},
    neuron/.style={circle,fill=black!25,minimum size=17pt,inner sep=0pt},
    input neuron/.style={neuron, fill=gray!50},
    output neuron/.style={neuron, fill=gray!50},
    hidden neuron/.style={neuron, fill=black!75},
    annot/.style={text width=4em, text centered}
]

    % Draw the input layer nodes
    \foreach \name / \y in {1,...,3}
    % This is the same as writing \foreach \name / \y in {1/1,2/2,3/3,4/4}
    \node[input neuron, pin=left:Input \#\y] (I-\name) at (0,-\y) {}; 

    % set number of hidden layers
    \newcommand\Nhidden{2}
    \newcommand\NodOne{13}
    \newcommand\NodTwo{13}
    \newcommand\NodThree{13}

    % Draw the hidden layer nodes
    \foreach \N in {0,...,\Nhidden} {
       \foreach \y in {0,...,5} { % <-- added 0 instead of 1
         \ifnum \y=4
           \node at (\N*\layersep,-\y cm) [hidden neuron] (H\N-\y) {};
         \else
           \node at (\N*\layersep,-\y cm) [neuron] (H\N-\y) {};
         \fi
       }
    \node[annot,above of=H\N-1, node distance=1cm] (hl\N) {Hidden layer \N};
    }

    % Draw the output layer node
    \node[output neuron,pin={[pin edge={->}]right:Output}, right of=H\Nhidden-3] (O) {}; 

    % Connect every node in the input layer with every node in the
    % hidden layer.
    \foreach \source in {1,...,3}
    \foreach \dest in {1,...,3,5} {
      \path (I-\source) edge (H0-\dest);
    }

    % connect all hidden stuff
    \foreach [remember=\N as \lastN (initially 0)] \N in {1,...,\Nhidden}
    \foreach \source in {0,...,5,7} {
      \foreach \dest in {1,...,3,5} {
        \path (H\lastN-\source) edge (H\N-\dest);
      }
    }

    % Connect every node in the hidden layer with the output layer
    \foreach \source in {1,...,3,5}
    \foreach \dest in {1,...,3,5} {
      \path (H\Nhidden-\source) edge (O);
      \path (H\Nhidden-\source) edge (H\N-\dest);
    }

    % Annotate the layers
    \node[annot,left of=hl1] {Input layer};
    \node[annot,right of=hl\Nhidden] {Output layer};
\end{tikzpicture}

\end{document}

