% Newcommand

%%Midline Label
\newcommand{\midlinelabel}[3]{
    \node (midlabel) at ($ (#1)!.5!(#2) $) {\tiny #3};
    \draw[<-, line width=1mm, line cap=round] (#1) -- (midlabel);
    \draw[->, line width=1mm, line cap=round] (midlabel) -- (#2);
}

% Grid
\newcommand{\grid}[1]{
    \draw[line width=0.05mm, draw=black!25, rounded corners=0.05mm] (#1) rectangle +(4.25, 2.5);
}

% Force Vectors
\newcommand{\forcevector}[3]{
    \draw[thick,->, line cap=round, line width=1mm] (#1) -- +(#2);
    \node[fill=white,rounded corners=0.2mm, line width=1mm, rotate=90, inner sep=2] at ($(#1)+#2/2$) {\small #3};
}

% Command for a dot in the middle
\newcommand{\bulb}{
    \node[circle, fill=lightblue, inner sep=0.05mm] at ($(midlabel)!.5!(midlabel -| #2)$) {};
}

% 3D Axis
\newcommand{\trajs}[2]{
    \draw[->, line width=1mm, line cap=round] ($(#1)+(2,0,0)$) -- +(#2) node[below, midway] {\small Trace};
    \draw[->, line width=1mm, line cap=round] ($(#1)+(0,1,0)$) -- +(0,#2) node[below, midway] {\small Direct};
    \draw[->, line width=1mm, line cap=round] ($(#1)+(0,0,1)$) -- +(0,0,#2) node[right, midway] {\small Up};
    \draw[->, line width=1mm, line cap=round] ($(#1)+(0,-1,0)$) -- +(0,#2,0) node[below, midway] {\small Trace};
    \draw[->, line width=1mm, line cap=round] (#1) -- +(0,#2,0) node[below, midway] {\small +};
    \draw[->, line width=1mm, line cap=round] ($(#1)+(0,-1,-1)$) -- +(0,#2,0) node[right, midway] {\small -};
    \draw[->, line width=1mm, line cap=round] (#1) -- +(0,0,-1) node[right, midway] {\small +};
    \draw[->, line width=1mm, line cap=round] (#1) -- +(0,0,-1) node[right, midway] {\small -};
    \draw[->, line width=1mm, line cap=round] (#1) -- +(0,1,-1) node[right, midway] {\small +};
    \draw[->, line width=1mm, line cap=round] (#1) -- +(0,1,-1) node[right, midway] {\small -};
}

