\documentclass[border=0.125cm]{standalone}
\usepackage{tikz}
\usetikzlibrary{math}

\begin{document}

\pgfdeclarelayer{background}
\pgfsetlayers{background,main}

\tikzmath{
let \n=10; % number of rows
let \m=10; % number of columns
\pgfmathsetmacro\w{4/(\n+1)/2}; % width of a grid
\pgfmathsetmacro\h{5/(\m+1)/2}; % height of a grid
\pgfmathsetmacro\l{max(\w,60pt)/2}; % a little bit longer edges than required
}

\begin{tikzpicture}
\tikzmath{ %
let \s=2;
% save some calculation for later
\wr=4/(\n+1)/2; % width of a grid
\sh{5/(\m+1)/2}; % height of a grid
\lr=max(\wr, \sh); % a little bit longer edges than required
% define the grid
\g-grid{
    % put the coordinates in a convenient way
    \foreach \i in {1,..., \n} {
        \foreach \j in {1,..., \m} {
            \coordinate (\i-\j) at (\i*\w-\w/2, -\j*\h-\s/2);
        }
    }

    % get the upper left and lower right coordinates
    \pgfmathparse{int(\n*\w-\wr)}
    \coordinate (ul) at (\pgfmathresult-\wr/2,\n*\w-\wr/2);
    \coordinate (ll) at (\pgfmathresult-\wr/2-\s, \n*\w-\wr/2-\s);

    % get the upper right and lower left coordinates
    \pgfmathparse{int(\m*\w-\wr)}
    \coordinate (ur) at (\pgfmathresult-\wr/2,\m*\w-\wr/2);
    \coordinate (lll) at (\pgfmathresult-\wr/2-\s, \m*\w-\wr/2-\s);

    % calculate the position of the grid
    \pgfmathparse{sqrt(\h*\h-\s*\s)}
    \coordinate (center) at (ll -| lln);

    % the grid
    \draw[line width=0.5pt] (ul) rectangle (ur);
    \pgfmathtruncatemacro\dimx{\n*\w-\wr}
    \pgfmathtruncatemacro\dimy{\m*\w-\wr}
    \pgfmathsetmacro\xoff{15*(\dimx+1)/2}
    \pgfmathsetmacro\yoff{15*(\dimy+1)/2}
    \begin{scope}[shift={(\xoff, \yoff)}]
        \draw[black!20] (-1,-1) grid ++(\dimx+1,\dimy+1);
        \draw[black!50,thick] (-1,-1) grid ++(\dimx,\dimy);
    \end{scope}

    % the hightlighted part
    \draw[rounded corners, fill=white] 
        (center) circle [radius=1/10] node {e};

    % the search path

    % the bounding box
    \draw[line width=0.5pt, rounded corners] 
        (center) ++ (0.1, 0.1) rectangle ++(-\w, -\h);

    % the two helper rectangles
    \draw[line width=0.5pt, rounded corners] 
        (center) ++ (1, 1) rectangle ++(4, 4);
    \draw[line width=0.5pt, rounded corners] 
        (center) ++ (-1,-1) rectangle ++(4, 4);

    % the two helper circles
    \draw[fill=white] (center) ++ (1, 1) circle [radius=1/10];
    \draw[fill=white] (center) ++ (-1,-1) circle [radius=1/10];
}

\begin{pgfonlayer}{background}
\clip[rounded corners] 
    (center) ++ (1, 1) rectangle ++(4, 4)
    (center) ++ (-1,-1) rectangle ++(4, 4) 
    [sharp corners] 
    (center) ++ (0.1, 0.1) rectangle ++(-\w, -\h);
\fill [opacity=0.1, blue!50]
    (center) circle [radius=1] ++(center) circle [radius=\w/2];
\end{pgfonlayer}
\end{tikzpicture}
\end{document}

