%%%%%% Grouping Commands

\newcommand{\mygroup}{
  \begin{tikzpicture}
    \foreach \x in {1,...,\n}%
    {%
      \sbox{\mybox}{\dotnode{\x}}% Get the dot and store in \sbox for convenience
      \ifnum \x < \n\relax
        \node (\x) at (\t*\x, 0) [circle, draw, minimum size = \r cm] {\two{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l, \t*\s1 + \d) [circle, draw, minimum size = \r cm] {\two{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l + \d, \d) [circle, draw, minimum size = \r cm] {\three{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l + \d + \w, \d + \s2 - \w) [circle, draw, minimum size = \r cm] {\four{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l + \d + \w + \s, \d + \s1 - \w - \s) [circle, draw, minimum size = \r cm] {\five{\sbox{\mybox}}};
      \else
        \node (\x) at (\t*\x, 0) [circle, draw, minimum size = \r cm] {\two{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l, \d) [circle, draw, minimum size = \r cm] {\three{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l + \d, \d) [circle, draw, minimum size = \r cm] {\four{\sbox{\mybox}}};
        \node (\x) at (\t*\x + \l + \d + \w, \d + \s2 - \w) [circle, draw, minimum size = \r cm] {\five{\sbox{\mybox}}};
      \fi
    }
  \end{tikzpicture}
}

