\fi
\node[anchor=center] at ($(\j+\i*\padding/2,\k*\padding/2)$) {$\j$};
\node[empty] at (\j-\padding/2) {};
}
\foreach \i/\j [count=\label] in {#2}
{
\draw[edge] (\i) -- node[topline] {\j} (\label);
}
\begin{scope}[shift={($(0,\padding+1.5em)$)}]
