\ifnum\i>0
\foreach \l/\m in {#2}
{
\pgfmathparse{\l-(\i-1)*2}
\node[empty] at ($(1,-\pgfmathresult)$) {};
}
\fi
\end{scope}
\begin{scope}[shift={($(0,-\padding-1.5em)$)}]
\node at (1-\i,0) {$\hat{0}$};
\ifnum\i>0
\foreach \l/\m in {#2}
{
\pgfmathparse{\l-(\i-1)*2}
\node[empty] at ($(1,-\pgfmathresult)$) {};
}
\fi
\end{scope}
}
\begin{scope}[shift={($(1,-2*\padding-1.5em)$)}]
\node at (1-\i,0) {$\hat{2}$};
\ifnum\i>0
\foreach \l/\m in {#2}
{
\pgfmathparse{\l-(\i-1)*2}
\node[empty] at ($(1,-\pgfmathresult)$) {};
}
\fi
\end{scope}
\begin{scope}[shift={($(1,-3*\padding-1.5em)$)}]
\node at (1-\i,0) {$\hat{3}$};
\ifnum\i>0
\foreach \l/\m in {#2}
{
