  \foreach \t in {0,1,2,3,4}{
    \pgfmathparse{
      ifthenelse(\t==0,4,1)
    }
    \pgfmathsetmacro{\xval}{\pgfmathresult}
    \pgfmathsetmacro{\yval}{4-0.5*\t}
