    \pgfmathsetmacro{\ynext}{\ystart+\sp+0.25}
    \draw[holdot] plot coordinates{(0.5*\xstart,\yval) (\xstart,\yval) (\xstart,\ynext)};
    \draw[soldot] plot coordinates{(0.5*\xnext,\yval) (\xnext,\yval) (\xnext,\ynext)};
    \draw[gray, dashed] (\xstart,\ystart) -- (\xstart,\yval) -- (\xnext,\yval);
