\newcommand{\logLogSlopeTriangle}[5]{
    \pgfplotsextra{
        \pgfkeysgetvalue{/pgfplots/xmin}{\xmin}
        \pgfkeysgetvalue{/pgfplots/xmax}{\xmax}
        \pgfkeysgetvalue{/pgfplots/ymin}{\ymin}
        \pgfkeysgetvalue{/pgfplots/ymax}{\ymax}

        \pgfmathsetmacro{\xArel}{#1}
        \pgfmathsetmacro{\yArel}{#3}
        \pgfmathsetmacro{\xBrel}{#1-#2}
        \pgfmathsetmacro{\yBrel}{\yArel}
        \pgfmathsetmacro{\xCrel}{\xArel}
        \pgfmathsetmacro{\yCrel}{\yBrel}
        \pgfmathsetmacro{\lnxB}{\xmin*(1-(#1-#2))+\xmax*(#1-#2)} \pgfmathsetmacro{\lnxA}{\xmin*(1-#1)+\xmax*#1} \pgfmathsetmacro{\lnyA}{\ymin*(1-#3)+\ymax*#3} \pgfmathsetmacro{\lnyC}{\lnyA+#4*(\lnxA-\lnxB)}
        \pgfmathsetmacro{\yCrel}{\lnyC-\ymin)/(\ymax-\ymin)} \coordinate (A) at (rel axis cs:\xArel,\yArel);
        \coordinate (B) at (rel axis cs:\xBrel,\yBrel);
        \coordinate (C) at (rel axis cs:\xCrel,\yCrel);
        \coordinate (D) at (rel axis cs:\lnxA,\lnyA);
        \coordinate (E) at (rel axis cs:\lnxB,\lnyB);
        \coordinate (F) at (rel axis cs:\lnxC,\lnyC);

        \draw[#5]   (A)-- node[pos=.5] {1}
                    (B)-- 
                    (C)-- node[pos=.5] {#4}
                    (D)-- 
                    (E)-- node[pos=.5] {#4}
                    (F)-- 
                    cycle;
    }
}
