\newcommand{\drawcluster}[3][\empty]{
\foreach \i in {1,...,#2}
    \foreach \j in {1,...,#2}
        \ifthenelse{\i=#1 \AND \j=#1 \AND \i+\j<#2}
            {\node[draw,circle,inner sep=1pt,fill=black] at (\i,\j) {};}  
        {\ifthenelse{\i=#1 \AND \j=#1 \AND \i+\j<#2}
            {\node[draw,circle,inner sep=1pt,fill=black] at (\i,\j) {};}  
        {\ifthenelse{\i=#1 \AND \j=#2 \AND \i+\j<#1}
            {\node[draw,circle,inner sep=1pt,fill=black] at (\i,\j) {};}  
        {\ifthenelse{\i=#2 \AND \j=#1 \AND \i+\j<#2}
            {\node[draw,circle,inner sep=1pt,fill=black] at (\i,\j) {};}  
        }}
}

\newcommand{\drawconnect}[3][\empty]{
\foreach \i in {1,...,#2}
    \foreach \j in {1,...,#2}
        \ifthenelse{\i=#1 \AND \j=#1 \AND \i+\j<#2}
            {}{\draw[->] (\i,\j) -- (\i+1,\j);}
        {\ifthenelse{\i=#1 \AND \j=#2 \AND \i+\j<#1}
            {}{\draw[->] (\i,\j) -- (\i,\j+1);}
        {\ifthenelse{\i=#2 \AND \j=#1 \AND \i+\j<#2}
            {}{\draw[->] (\i,\j) -- (\i+1,\j);}
        }
}

\newcommand{\drawforce}[3][\empty]{
\foreach \i in {1,...,#2}
    \foreach \j in {1,...,#2}
        \ifthenelse{\i=#1 \AND \j=#1 \AND \i+\j<#2}
            {\node[draw,circle,fill=gray!25] at (\i,\j) {};}  
        {\ifthenelse{\i=#1 \AND \j=#2 \AND \i+\j<#1}
            {\node[draw,circle,fill=gray!25] at (\i,\j) {};}  
        {\ifthenelse{\i=#2 \AND \j=#1 \AND \i+\j<#2}
            {\node[draw,circle,fill=gray!25] at (\i,\j) {};}  
        }
}

\newcommand{\drawnetwork}[3][\empty]{
\foreach \i [count=\ii] in {1,...,#2}
    \foreach \j [count=\jj] in {1,...,#2}
        \drawcluster{\i}{\j}{\ii}
        \foreach \a in {1,...,#2}
            \drawconnect{\a}{\j}
        \foreach \b in {1,...,#2}
            \drawforce{\b}{\j}
}

\newcommand{\network}[1]{
\foreach \a/\b/\c in {#1}
    \drawnetwork{\a,\b,\c}
}

