\newcommand{\TikZMGClockMath}[4][\circ]{%
    % #1 = optional scaling factor (default is 1.0)
    % #2 = node name for the "running total"
    % #3 = node name for the "running sum"
    % #4 = number of time cycles
    \foreach \N [remember=\N as \LastN (initially 0)] in {1,...,#4} {
        \draw[thick,#1] (\LastN*3mm-4*#2*3mm,0) arc(0:180:#2*2*#3*1.05);
    }
    \node[above=-1.5pt] at (#2) {\pgfmathprint{int(#4)}};
    \node[mydot] (#3) at (#2) {};
    \foreach \N [remember=\N as \LastN (initially 0)] in {1,...,#4} {%
        \draw[thick] (#3) ++ ({\N*3mm-2*#3*3mm},0) arc(0:180:#3*1.05);
        \node[mydot] at (#3) {};
    }
    \draw[-latex,line width=1.5] (#2) ++ (0,2*#3*1.2) to[bend left=35] ++ (0,#3*1.2);% vertical line for the clock face
    \draw[-latex,line width=1.5] (#2) ++ (-2*#3*1.1,0) to[bend right=40] ++ (-0.5*#3*1,-#3*1.1);% horizontal line to place the 0
}

