  \expandafter\edef\csname sat#1\endcsname{\expandafter{\satnode}}%
  \begingroup
    \def\@tempa##1##2{%
      \ifnum##2=0\else\expandafter\@tempa\fi##1##2}%
    \def\@tempb{#1}%
    \@tempb
    \ifnum\@tempa>\@tempb
      \edef\@tempb{\the\numexpr##2 - 1\relax}%
    \fi
    \edef\@tempc{#1}%
    \@tempc
    \ifnum\@tempa>\@tempc
      \edef\@tempc{\the\numexpr##2 + 1\relax}%
    \fi
  \endgroup}
  \edef\@tempa{%
    \noexpand\createsatnode{#1}%
    \noexpand\def\nodetext{#1}%
    \noexpand\def\nodenum{#1}}
  \expandafter\expandafter\expandafter\gdef\csname sat#1\endcsname{\@tempa}%
  \gdef\@tempa{0}%
  \expandafter\edef\csname sat#1\endcsname{%
    \expandafter\satnode\expandafter{\nodetext}%
  }
}

