    % The style to draw a diagonal line including labels
    diagonal/.style={
        dashed,
        shorten >=0.2cm,
        shorten <=0.2cm
    },
    % The style to draw a horizontal line
    horizontal/.style={
        shorten >=0.2cm,
    },
    % The style to draw a vertical line
    vertical/.style={
        shorten <=0.2cm,
    },
    % Style to draw a circle around a node (vertical lines are not included)
    circle vertical line/.style={
        insert path={+(0,0) circle (2.5pt)}
    },
    % Style to draw a rectangle (horizontal lines are not included)
    rectangle horizontal line/.style={
        insert path={+(0,-0.9) rectangle +(0,-0.2)}
    },
    % The size of the Circuits
    circuit size/.style={
        minimum width=1.5cm,
        minimum height=1.5cm
    },
    % A style to draw colored rectangles (without a box)
    colored rectangle/.style={
        rectangle,
        draw,
        fill=#1,
        rounded corners,
    },
    % A style to draw colored circles (without a box)
    colored circle/.style={
        circle,
        draw,
        fill=#1,
        rounded corners,
    },
    % Styles to draw thick arrows
    thick arrow/.style={
        decoration={
            show path construction,
            lineto code={
                \draw[-latex,line width=.1em,black] let \p0 = (LinetoNode), \p1 = (LinetoNode-Body) in (\x1,\y0) -- (\x1,\y1);
            }
        },
        postaction=decorate
    },
    % The style to draw colored lines in the middle of a path
    colored line/.style={
        line width=.1em,
        black, 
        dashed,
        shorten >=0.2cm,
        shorten <=0.2cm,
        -latex,
        postaction=decorate
    },
    % The style to draw colored lines at the ends of a path
    colored line start/.style={
        line width=.1em,
        black, 
        dashed,
        shorten >=0.2cm,
        shorten <=0.2cm,
        -latex,
    },
    % The style to draw colored lines at the middle of a path
    colored line end/.style={
        line width=.1em,
        black, 
        dashed,
    },
}

