  \pgfmathsetmacro{\n}{5}

  \foreach \s in {1,...,\n}{

    % Set the color for the vertex
    \pgfmathparse{Mod(\s-1,2)? "cyan" : "magenta"}
    \edef\col{\pgfmathresult}

    % Draw the vertex
    \node[v\col] (\s) at (\s,0) {\s};

  }

  % Connect vertices with edges
  \foreach \s in {1,...,\n}{
