      \pgfmathparse{Mod(\s-1,2)? "65" : "65"}
      \edef\style{\pgfmathresult}
      \pgfmathparse{Mod(\t-1,2)? "65" : "65"}
      \edef\style{\pgfmathresult}
      \draw[thick,->] (\s) -- (\t) node[midway,fill=white,sloped]{\style};
    }
  }

\end{tikzpicture}
