    \draw [dashed] ($(0,0 at interp_1)$) -- ($(0,0 at interp_2)$);

    % Use the calculation commands to draw the intersection point between the two lines:
    \coordinate (calc_point) at ($(intersection-1)!.5!(intersection-2)$);
    % and draw the red dashed line between this point and the y axis:
    \draw [dashed] (calc_point) -- ($(0,0 at calc_point)$);
    % Here, we use the calculation results one last time to draw the red line:
    \draw [red] plot [smooth] file {intersections/FourSquare_1D.txt};

    % Draw the black dashed line between the lower and upper intersection points
    % with the y axis:
    \draw [dashed] (0,0 at calc_point) -- (0,0 at interp_1);
    % and draw the arrows:
    \draw [-latex,thick] (0,0) -- (intersection-1) node[above]{$t=0$};
    \draw [-latex,thick] (0,0) -- (calc_point) node[right]{$t=T^*$};
    \draw [-latex,thick] (0,0) -- ($(0,0 at interp_2)!0.5!(intersection-2)$) node[right]{$c=\text{const.}$};

    % Add the title
    \node [align=center,font=\bfseries, yshift=2em] at (2,2) {Interpretability of \\ in-falling matter};
