    \edef\mya{\csname mya\pgfplotspointmeta\endcsname}
    \edef\myb{\csname myb\pgfplotspointmeta\endcsname}
    \edef\myc{\csname myc\pgfplotspointmeta\endcsname}

    \pgfplotsset{
        % define a style for the `intersections' coordinate groups
        % (the default style has four such groups)
        % (the number of groups is given by the following formula
        %  (which is a power of 2)
        %  2 power 2 = 4)
        /pgfplots/coords/intersections/.style={
            % use the `get' command to extract the coordinates
            get coord/.code={\pgfmathsetmacro{\xstart}{\mya}\get{xstart}}
            get coord/.code={\pgfmathsetmacro{\xstart}{\myb}\get{xstart}}
            get coord/.code={\pgfmathsetmacro{\xstart}{\myc}\get{xstart}}
            % now we can draw the lines as normal lines...
            draw=none,
            %... but you can use the `intersections' as normal intersections coordinates
            name intersections={
                % we first draw the lower line
                lower intersection/.style={
                    % we want a line from the start to the start + 1
                    name path=lower line,
                    get coord/.code={\pgfmathsetmacro{\myxstart}{\mya + 1}\get{xstart}}
                },
                % we then draw the upper line
                upper intersection/.style={
                    % we want a line from the start to the start - 1
                    name path=upper line,
                    get coord/.code={\pgfmathsetmacro{\myxstart}{\mya - 1}\get{xstart}}
                },
            },
        },
        % define the style for the "main" coordinate groups
        % (the default style has five such groups)
        /pgfplots/coords/normal/.style={
            % we only want the lower line
            lower intersection/.style={
                % the style is dependent on the previous style
                and (
                    name intersections={
                        lower intersection,
                    } > {0},
                    name path=main line,
                    % now we draw the upper line
                    name intersection={
                        upper intersection,
                    },
                ),
            },
            % we also want the upper line...
            upper intersection/.style={
                name path=upper line,
            },
        },
        % define the style for the "extra" coordinate groups
        /pgfplots/coords/extra/.style={
            % we only want the lower line
            lower intersection/.style={
                name path=lower extra line,
            },
            % we also want the upper line
            upper intersection/.style={
                name path=upper extra line,
            },
        },
    ]
